_ctrl [ctrlSetPosition](https://community.bistudio.com/wiki/ctrlSetPosition) [0, 0, 0.5, 0.5];
_ctrl [ctrlSetBackgroundColor](https://community.bistudio.com/wiki/ctrlSetBackgroundColor) [0, 0, 0, 1]; 
_ctrl [ctrlCommit](https://community.bistudio.com/wiki/ctrlCommit) 0;
[private](https://community.bistudio.com/wiki/private) _lineHeight = [getNumber](https://community.bistudio.com/wiki/getNumber) ([configFile](https://community.bistudio.com/wiki/configFile) >> "RscStructuredText" >> "size");
[private](https://community.bistudio.com/wiki/private) _linesTotal = ([ctrlPosition](https://community.bistudio.com/wiki/ctrlPosition) _ctrl [select](https://community.bistudio.com/wiki/select) 2) / _lineHeight;
[private](https://community.bistudio.com/wiki/private) _trailingSpace = "";
[for](https://community.bistudio.com/wiki/for) "_i" [from](https://community.bistudio.com/wiki/from) 1 [to](https://community.bistudio.com/wiki/to) _linesTotal [do](https://community.bistudio.com/wiki/do) { _trailingSpace = _trailingSpace + " " };
_ctrl ctrlSetStructuredText [parseText](https://community.bistudio.com/wiki/parseText) [format](https://community.bistudio.com/wiki/format) ["<t size='%1'><t size='1' align='center' valign='middle'>%2%3</t> </t>", _linesTotal, "------ Centered Text ------", _trailingSpace];
