```sqf
// Returns array of dates for given year when moon is at its fullest
fnc_fullMoonDates = 
{
	[private](https://community.bistudio.com/wiki/private) _year = [param](https://community.bistudio.com/wiki/param) [0, 2035];
	[private](https://community.bistudio.com/wiki/private) ["_date", "_phase", "_fullMoonDate"];
	[private](https://community.bistudio.com/wiki/private) _fullMoonPhase = 1;
	[private](https://community.bistudio.com/wiki/private) _waxing = [false](https://community.bistudio.com/wiki/false);
	[private](https://community.bistudio.com/wiki/private) _fullMoonDates = [];
	[for](https://community.bistudio.com/wiki/for) "_i" [from](https://community.bistudio.com/wiki/from) [dateToNumber](https://community.bistudio.com/wiki/dateToNumber) [_year, 1, 1, 0, 0] [to](https://community.bistudio.com/wiki/to) [dateToNumber](https://community.bistudio.com/wiki/dateToNumber) [_year, 12, 31, 23, 59] [step](https://community.bistudio.com/wiki/step) 1 / 365 [do](https://community.bistudio.com/wiki/do)
	{
		_date = [numberToDate](https://community.bistudio.com/wiki/numberToDate) [_year, _i];
		_phase = moonPhase _date;
		[call](https://community.bistudio.com/wiki/call)
		{
			[if](https://community.bistudio.com/wiki/if) (_phase > _fullMoonPhase) [exitWith](https://community.bistudio.com/wiki/exitWith)
			{
				_waxing = [true](https://community.bistudio.com/wiki/true);
				_fullMoonDate = _date;
			};
			[if](https://community.bistudio.com/wiki/if) (_waxing) [exitWith](https://community.bistudio.com/wiki/exitWith) 
			{
				_waxing = [false](https://community.bistudio.com/wiki/false);
				_fullMoonDates [pushBack](https://community.bistudio.com/wiki/pushBack) _fullMoonDate;
			};
		};
		_fullMoonPhase = _phase;
	};
	_fullMoonDates
};

//set random full moon date in year 1970
[setDate](https://community.bistudio.com/wiki/setDate) [selectRandom](https://community.bistudio.com/wiki/selectRandom) (1970 [call](https://community.bistudio.com/wiki/call) fnc_fullMoonDates);
```

